/\*\* Parses CSVs into a 2D array. \*/
const parseCsv = (strData: string) => {
 const strDelimiter = ","

 var objPattern = new RegExp(
 // Delimiters
 "(\\" +
 strDelimiter +
 "|\\r?\\n|\\r|^)" +
 // Quoted fields
 '(?:"([^"]\*(?:""[^"]\*)\*)"|' +
 // Standard fields
 '([^"\\' +
 strDelimiter +
 "\\r\\n]\*))",
 "gi"
 )

 var arrData: string[][] = [[]]

 // Create an array to hold our individual pattern matching groups.
 var arrMatches: RegExpExecArray | null = null

 // Keep looping over the regular expression matches until we can no longer
 // find a match.
 while ((arrMatches = objPattern.exec(strData))) {
 const strMatchedDelimiter = arrMatches[1]

 // Check to see if strMatchedDelimiter has a length (is not the start of
 // string) and if it matches strDelimiter. If id does not, then we know
 // that this delimiter is a row delimiter.
 if (strMatchedDelimiter.length && strMatchedDelimiter !== strDelimiter) {
 // Since we have reached a new row of data, add an empty row to our data
 // array.
 arrData.push([])
 }

 var strMatchedValue = ""

 // Now that we have our delimiter out of the way, let's check to see which
 // kind of value we captured (quoted or unquoted).
 if (arrMatches[2]) {
 // We found a quoted value. When we capture this value, unescape any
 // double quotes.
 strMatchedValue = arrMatches[2].replace(new RegExp('""', "g"), '"')
 } else {
 // We found a non-quoted value.
 strMatchedValue = arrMatches[3]
 }

 arrData[arrData.length - 1].push(strMatchedValue)
 }

 return arrData
}
